import{autocomplete}from"https://cdn.jsdelivr.net/npm/@algolia/autocomplete-js/+esm";import algoliasearch from"https://cdn.jsdelivr.net/npm/algoliasearch@4.22.1/+esm";const searchClient=algoliasearch("DA85FAYBEU","cca2b9e467771c47e8c64b7ac2acdbe0"),indices=[{name:"wp_admin_posts",label:"Article"},{name:"wp_admin_pages",label:"Page"},{name:"wp_admin_projects",label:"Projet"},{name:"wp_admin_programmes",label:"Programme"},{name:"wp_admin_communiques",label:"Communiqué"},{name:"wp_admin_equipes",label:"Équipe"}];autocomplete({container:"#autocomplete",placeholder:"Rechercher...",getSources:({query:e})=>indices.map((({name:a,label:t})=>({sourceId:a,getItems:()=>searchClient.initIndex(a).search(e).then((({hits:e})=>e.map((e=>({...e,_type:t}))))),templates:{header:()=>`<span style="font-size:0.9em;color:#888">${t}s</span>`,item:({item:e})=>`\n            <a href="${e.permalink||e.url}" style="display:block;padding:0.5em 0">\n              <strong>${e.post_title||e.title}</strong>\n              <span style="font-size:0.85em;color:#666;margin-left:0.5em">[${e._type}]</span>\n              <div style="font-size:0.9em;color:#666">${e.post_excerpt||e.excerpt||""}</div>\n            </a>\n          `}}))),onSubmit({state:e}){window.location.href=`/recherche?q=${encodeURIComponent(e.query)}`}});