import algoliasearch from"https://cdn.jsdelivr.net/npm/algoliasearch@4.22.1/+esm";const searchClient=algoliasearch("DA85FAYBEU","cca2b9e467771c47e8c64b7ac2acdbe0"),indices=[{name:"wp_admin_posts",label:"Article"},{name:"wp_admin_pages",label:"Page"},{name:"wp_admin_projects",label:"Projet"},{name:"wp_admin_programmes",label:"Programme"},{name:"wp_admin_communiques",label:"Communiqué"},{name:"wp_admin_equipes",label:"Équipe"}],params=new URLSearchParams(window.location.search),q=params.get("q")||"";async function searchAll(){if(!q)return;let e=[];for(const{name:a,label:n}of indices){const t=searchClient.initIndex(a),{hits:i}=await t.search(q);e=e.concat(i.map((e=>({...e,_type:n}))))}return e}searchAll().then((e=>{const a=document.getElementById("search-results");e&&0!==e.length?a.innerHTML=e.map((e=>`\n    <div style="margin-bottom:2em">\n      <a href="${e.permalink||e.url}">\n        <h2>${e.post_title||e.title}</h2>\n      </a>\n      <span style="font-size:0.9em;color:#666">[${e._type}]</span>\n      <div>${e.post_excerpt||e.excerpt||""}</div>\n    </div>\n  `)).join(""):a.innerHTML="<p>Aucun résultat trouvé.</p>"}));